const items = $input.first().json.data.rows;

const result_list = items.map((item) => {
  return {
    rowid: item.rowid,
    video_name: item.video_name,
    url: item.url,
    sys_id: item.sys_id,
    fanolab_id: item.fanolab_id
  };
});

return result_list;

var target_row_id = $('Loop Over Items3').first().json.rowid
var transcription = $input.first().json.transcriptions
var speaker_stats = $input.first().json.speaker_stats
var speaker_list = []
for (const speaker in speaker_stats) {
  if (speaker_stats.hasOwnProperty(speaker)) {
      speaker_list.push({
          speaker_id: speaker,
          ...speaker_stats[speaker]
      });
  }
}
var total_duration = $input.first().json.total_duration


var output = {
  rowid: target_row_id, 
  total_duration: total_duration,
  speaker_stats: speaker_list,
  transcriptions: transcription }

return output

var speaker_stats = $input.first().json.speaker_stats;
var resultArray = [];

for (const speaker of speaker_stats) {
    var output_obj = [
        { "controlId": "speaker", "value": speaker.speaker_id },
        { "controlId": "meeting_minutes", "value": $input.first().json.rowid }, // Assuming static value
        { "controlId": "wpm", "value": speaker.words_per_minute.toFixed(2) },
        { "controlId": "talk_time", "value": (speaker.percentage / 100).toFixed(2) },
        { "controlId": "total_talk_time", "value": (speaker.total_duration / 60).toFixed(2) }
    ]
        if (speaker.identified_name !== "unknown") {
            output_obj.push({ "controlId": "name", "value": speaker.identified_name });
        }

    resultArray.push(output_obj);
}

return {data: resultArray};

const items_list = $input.first().json.data.rows;

var output_list = []

for (const item of items_list) {
  var output_obj = {
    rowid: item.rowid,
    sys_id: item.sys_id,
    datetime: item.datetime,
    project: item.project,
    description: item.description,
    transcript: item.transcript
  }
  output_list.push(output_obj)
}

// Sort by datetime (assuming datetime is in a format that can be sorted correctly as a string)
output_list.sort((a, b) => new Date(a.datetime) - new Date(b.datetime));


return output_list;

const items = $input.all();
const updatedItems = items.map((item) => {
  // const textArray = $input.first().json.text.split("\n");
  const textArray = $input.first().json.output.output_text.split("\n");
  return { textArray };
});
return updatedItems;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one

var output_list = []
for (const item of $input.first().json.data.rows) {
  var meeting_dt = item.datetime
  var meeting_memory = item.memory
  var output = {meeting_dt: meeting_dt, meeting_memory: meeting_memory}
  output_list.push(output)
}

return {output_list};

